import data from '../data';
import React from 'react';
import { useState, useEffect } from 'react';

import Paper from '@mui/material/Paper';
import InputBase from '@mui/material/InputBase';
import IconButton from '@mui/material/IconButton';

import Chip from '@mui/material/Chip';
import Avatar from '@mui/material/Avatar';
import "./Messages.css"

function Messages() {
  const { messagesData } = data;

  const [input, setInput] = useState('');
  const [sorted, setSorted] = useState(messagesData);

  function SearchBtn() {
    return (
      <IconButton>
        <svg
          xmlns='http://www.w3.org/2000/svg'
          x='0px'
          y='0px'
          width='20'
          height='20'
          viewBox='0 0 30 30'
        >
          <path d='M 13 3 C 7.4889971 3 3 7.4889971 3 13 C 3 18.511003 7.4889971 23 13 23 C 15.396508 23 17.597385 22.148986 19.322266 20.736328 L 25.292969 26.707031 A 1.0001 1.0001 0 1 0 26.707031 25.292969 L 20.736328 19.322266 C 22.148986 17.597385 23 15.396508 23 13 C 23 7.4889971 18.511003 3 13 3 z M 13 5 C 17.430123 5 21 8.5698774 21 13 C 21 17.430123 17.430123 21 13 21 C 8.5698774 21 5 17.430123 5 13 C 5 8.5698774 8.5698774 5 13 5 z'></path>
        </svg>
      </IconButton>
    );
  }
  function inputHandle(e) {
    setInput(e.target.value.toLowerCase());
  }

  useEffect(() => {
    const newData = messagesData.filter((item) => {
      const name = item.name.toLowerCase();
      return name.search(input) >= 0;
    });
    setSorted(newData);
  }, [input, messagesData]);

  return (
    <div className='messagesList'>
      <div className='messagesHeading'>
        <div className='messagesTitle'>
          <p className='headingSemi'>Messages</p>{' '}
          <Chip label='2' size='small' />
        </div>
        <Paper
          component='form'
          elevation={0}
          sx={{ display: 'flex', alignItems: 'center' }}
        >
          <SearchBtn></SearchBtn>
          <InputBase
            placeholder='Search'
            value={input}
            onChange={(e) => {
              inputHandle(e);
            }}
          />
        </Paper>
      </div>

      <div className='messagesListContent'>
        {sorted.length ? (
          sorted.map((message) => {
            const { id, name, avatar, recentmsg, totalmsgs } = message;
            return (
              <div className='messageItem' key={id}>
                <div className='leftSec'>
                  <Avatar src={avatar} sx={{ height: '60px', width: '60px' }} />
                  <div className='messageDetails'>
                    <p className='headingFull'>{name}</p>
                    <p className='headingFade' style={{ color: 'inherit' }}>
                      {recentmsg}
                    </p>
                  </div>
                </div>
                <div className='rightSec'>
                  <p className='headingFade'>3:34pm</p>
                  <Chip
                    label={totalmsgs ? totalmsgs : 'Your turn'}
                    size='small'
                  />
                </div>
              </div>
            );
          })
        ) : (
          <div className='messageItem'>
            <div className='leftSec'>
              <Avatar alt='?' sx={{ height: '55px', width: '55px' }} />
              <div className='messageDetails'>
                <p className='headingFull'>No Users Exists </p>
                <p className='headingFade' style={{ color: 'inherit' }}>
                  Please try using different set of words.
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Messages;
